mapper.select:
	SELECT *
	 FROM( 
		  SELECT A.*, rownum r
	  	  FROM(
	  	  	   select 
	  	  	     from wms_indiv t1

			   		t1.id=#{id}
			   		and t1.indiv_code=#{indivCode}
			   		and t1.warehouse_id=#{warehouseId}
			   		and t1.sku_id=#{skuId}
			   		and t1.wares_status=#{waresStatus}
			   		and t1.stock_status=#{stockStatus}
			   		and t1.stockin_item_id=#{stockInItemId}
			   		and t1.out_code=#{outCode}
			   		and t1.sku_code=#{skuCode}
			   		and t1.sku_name like '%${skuName}%'
			   		and t1.in_time>=#{stockInTimeBegin}
			   		and t1.in_time<=#{stockInTimeEnd}

	  	  	   ) A

		  WHERE rownum<=#{page.endRow} 
		)
    WHERE r >= #{page.startRow}

	select count(1) as total
	  from wms_indiv t1

   		t1.id=#{id}
   		and t1.indiv_code=#{indivCode}
   		and t1.warehouse_id=#{warehouseId}
   		and t1.sku_id=#{skuId}
   		and t1.wares_status=#{waresStatus}
   		and t1.stock_status=#{stockStatus}
   		and t1.stockin_item_id=#{stockInItemId}
   		and t1.stockout_code=#{stockOutCode}
   		and t1.stockout_item_id=#{stockOutItemId}
   		and t1.sku_code=#{skuCode}
   		and t1.sku_name like '%${skuName}%'
   		and t1.in_time>=#{stockInTimeBegin}
   		and t1.in_time<=#{stockInTimeEnd}

select 
  from wms_indiv t1

	t1.out_id=#{outId}
	and t1.order_code=#{orderCode}
	and t1.prepare_id=#{prepareId}
	and t1.sku_code=#{skuCode}
	and t1.warehouse_id=#{wareHouseId}
	and t1.stock_status!=#{stockStatusNotIn}

SELECT * FROM( SELECT A.*, rownum r FROM( select t3.material_code as materialCode, t1.indiv_code as indivCode, t1.out_time as outTime, t1.push_status as pushStatus, t1.push_time as pushTime, t1.push_count as pushCount, t1.out_id as "order.id", t2.province as "order.province", t2.city as "order.city", t2.district as "order.district", t2.address as "order.address", t2.consignee as "order.consignee", t2.mobile as "order.mobile", t2.tel as "order.tel", t1.order_code as orderCode from wms_indiv t1 join wms_sales_order t2 on t1.out_id = t2.id join wms_sku t3 on t1.sku_id = t3.id
t1.stock_status = 4 and t1.push_status != '1'
) A
WHERE rownum<=#{page.endRow} ) WHERE r >= #{page.startRow}

