mapper.select:
	SELECT *
	 FROM( 
		  SELECT A.*, rownum r
	  	  FROM(

				  from wms_indiv_flow t1

			   		t1.id=#{id}
			   		and t1.flow_type=#{flowType}
			   		and t1.indiv_code=#{indivCode}
			   		and t1.sku_id=#{skuId}
			   		and t1.wares_status=#{waresStatus}
			   		and t1.flow_goods_id=#{flowItemId}
			   		and t1.flow_code=#{flowCode}

	  	  	   ) A

		  WHERE rownum<=#{page.endRow} 
		)
    WHERE r >= #{page.startRow}

	select count(1) as total
	  from wms_indiv_flow t1
	  join wms_sku t2
	    on t1.sku_id = t2.id
 left join wms_warehouse t4
	    on t1.warehouse_code = t4.warehouse_code

   		t1.id=#{id}
   		and t1.flow_type=#{flowType}
   		and t1.indiv_code=#{indivCode}
   		and t1.sku_id=#{skuId}
   		and t1.wares_status=#{waresStatus}
   		and t1.flow_goods_id=#{flowItemId}
   		and t1.flow_code=#{flowCode}


	  from wms_indiv_flow t1

   		t1.id=#{id}
   		and t1.flow_type=#{flowType}
   		and t1.flow_id=#{flowId}
   		and t1.flow_code=#{flowCode}
   		and t1.flow_goods_id=#{flowItemId}
   		and t1.indiv_code=#{indivCode}
   		and t1.sku_id=#{skuId}
   		and t1.wares_status=#{waresStatus}

mapper.insert:
insert into wms_indiv_flow(id, indiv_code, sku_id,sku_code,sku_name,warehouse_id,warehouse_name,flow_type,flow_id,flow_code,flow_goods_id,flow_time,wares_status,measure_unit,product_batchno,enabled,remark，case_code) select WMS_SEQ_COMMON.nextval,A.* from(
select #{item.indivCode, jdbcType=VARCHAR}, #{item.skuId, jdbcType=VARCHAR}, #{item.skuCode, jdbcType=VARCHAR}, #{item.skuName, jdbcType=VARCHAR}, #{item.warehouseId, jdbcType=VARCHAR}, #{item.warehouseName, jdbcType=VARCHAR}, #{item.flowType, jdbcType=VARCHAR}, #{item.flowId, jdbcType=VARCHAR}, #{item.flowCode, jdbcType=VARCHAR}, #{item.flowGoodsId, jdbcType=VARCHAR}, #{item.flowTime, jdbcType=VARCHAR}, #{item.waresStatus, jdbcType=VARCHAR}, #{item.measureUnit, jdbcType=VARCHAR}, #{item.productBatchNo, jdbcType=VARCHAR}, #{item.enabled, jdbcType=VARCHAR}, #{item.remark, jdbcType=VARCHAR}, #{item.caseCode, jdbcType=VARCHAR} from dual
)A

