mapper.insert:
insert into wms_sales_order (id,order_code,shipping_id,shipping_name,order_time,handled_by,handled_time,order_status, order_source,consignee,province,city,district,address,mobile,invoice_enabled,remark,type) select WMS_SEQ_COMMON.nextval,A.* from (
select #{item.orderCode},#{item.shippingId},#{item.shippingName},#{item.orderTime},#{item.handledBy},#{item.handledTime}, #{item.orderStatus},#{item.orderSource},#{item.consignee},#{item.province},#{item.city},#{item.district},#{item.address}, #{item.mobile},#{item.invoiceEnabled},#{item.remark},#{item.type} from dual
)A

insert into wms_sales_order_goods(id, order_id,sku_id, sku_code,sku_name,unit_price,quantity,measure_unit,subtotal_price,indiv_enabled,goods_sid) select WMS_SEQ_COMMON.nextval,A.* from (
select #{item.order.id},#{item.skuId},#{item.skuCode},#{item.skuName},#{item.unitPrice},#{item.quantity},#{item.measureUnit},#{item.subtotalPrice},#{item.indivEnabled},#{item.goodsSid, jdbcType=VARCHAR} from dual
)A

