mapper.select:
select distinct * from( select c.* from (select b.* from ( select t2.id goods_id, t2.sku_id, t2.sku_code, t2.sku_name, t2.unit_price, t2.quantity, t2.subtotal_price, t2.measure_unit, t1.id order_id, t1.order_code, t1.order_user, t1.order_time, t1.payment_type, t1.consignee, t1.province, t1.city, t1.district, t1.address, t1.batch_code, t1.delivery_code, t1.shipping_id, t1.shipping_no, t1.order_notify_count, t1.order_source, t1.payment_name, t1.order_amount, t1.shipping_name, t1.tel, t1.mobile, t1.weight, t1.remark, t1.order_status, t1.invoice_enabled, t1.invoice_type, t1.invoice_title, t1.invoice_amount, t1.invoice_content, t1.invoice_status, t1.type, rownum from wms_sales_order_goods t2 join wms_sales_order t1 on t2.order_id = t1.id
) b where 1=1
and b.order_code = #{orderCode}
and b.mobile = #{mobile}
and b.order_time >= #{orderTimeBegin}
and b.order_time <= #{orderTimeEnd}
) c left join wms_indiv d on (c.order_id = d.order_id) where 1=1
and d.indiv_code = #{indivCode}
and d.sku_name like '%${skuName}%'
) order by order_time desc

