mapper.select:
select t1.id as id, t1.supplier_code as supplierCode, t1.supplier_name as supplierName, t1.supplier_address as supplierAddress, t1.supplier_phone as supplierPhone, t1.supplier_contact as supplierContact, t1.default_status as defaultStatus, t1.create_time as createTime, t1.remark as remark, t1.enabled as enabled from wms_supplier t1
t1.id = #{id} and t1.supplier_code = #{supplierCode} and t1.supplier_name = #{supplierName} and t1.default_status = #{defaultStatus} and t1.enabled = #{enabled}
order by default_status desc

mapper.update:
update wms_supplier
supplier_address=#{supplierAddress}, supplier_phone=#{supplierPhone}, supplier_contact=#{supplierContact}, default_status=#{defaultStatus}, remark=#{remark}, enabled=#{enabled},
id = #{id} and supplier_code = #{supplierCode}
