mapper.select:
      SELECT * FROM (
        SELECT
        T1.ID                   AS transferId,
        T3.WAREHOUSE_NAME       AS transferTo,
        T1.STATUS               AS status,
        T1.CREATE_TIME          AS createTime,
        T1.HANDLED_BY           AS handledBy,
        T1.CONSIGNEE            AS consignee,
        T1.CONTACT              AS contact,
        T1.ORDER_AMOUNT         AS orderAmount,
        T1.TRANS_TYPE           AS transType,
        T1.REMARK               AS remark,
        T1.ORDER_PUSH_STATUS    AS orderPushStatus,
        T1.ORDER_CONFIRM_STATUS AS orderConfirmStatus,
        T2.INDIV_CODE           AS logisticName,
        T2.WAREHOUSE_NAME       AS warehouseName,
        T2.SKU_CODE             AS tSale,
        T2.SKU_NAME             AS tSend,
        1                       AS QUANTITY
        FROM WMS_TRANSFER T1
        LEFT JOIN WMS_INDIV T2 ON T1.ID = T2.PREPARE_ID
        LEFT JOIN WMS_WAREHOUSE T3 ON T1.TRANSFER_TO = T3.ID
		
			t1.id=#{transferId}
			and t1.warehouse_id=#{warehouseId}
			and t1.status=#{status}
			and t1.transfer_to=#{transferTo}
			and t1.order_push_status=#{orderPushStatus}
            and t1.LOGISTIC_NO = #{logisticNo}
            and t1.create_time >= #{createTimeBegin,jdbcType=DATE}
            and t1.create_time <= #{createTimeEnd,jdbcType=DATE}
	   		and t1.shipping_time>=#{shippingTimeBegin,jdbcType=DATE}
	   		and t1.shipping_time<=#{shippingTimeEnd,jdbcType=DATE}
            AND T1.TRANSFER_TO IN ()
		
      UNION ALL
        SELECT
        T1.ID                   AS transferId,
        T3.WAREHOUSE_NAME       AS transferTo,
        T1.STATUS               AS status,
        T1.CREATE_TIME          AS createTime,
        T1.HANDLED_BY           AS handledBy,
        T1.CONSIGNEE            AS consignee,
        T1.CONTACT              AS contact,
        T1.ORDER_AMOUNT         AS orderAmount,
        T1.TRANS_TYPE           AS transType,
        T1.REMARK               AS remark,
        T1.ORDER_PUSH_STATUS    AS orderPushStatus,
        T1.ORDER_CONFIRM_STATUS AS orderConfirmStatus,
        ''                      AS logisticName,
        T4.WAREHOUSE_NAME       AS warehouseName,
        T2.SKU_CODE             AS tSale,
        T2.SKU_NAME             AS tSend,
        T2.QUANTITY
        FROM
        WMS_TRANSFER T1
        LEFT JOIN WMS_TRANSFER_GOODS T2 ON T1."ID" = T2.TRANSFER_ID
        LEFT JOIN WMS_WAREHOUSE T3 ON T1.TRANSFER_TO = T3.ID
        LEFT JOIN WMS_WAREHOUSE T4 ON T1.WAREHOUSE_ID = T4.ID
      
        T2.SKU_CODE LIKE '2%'
      	and t1.id=#{transferId}
      	and t1.warehouse_id=#{warehouseId}
      	and t1.status=#{status}
      	and t1.transfer_to=#{transferTo}
      	and t1.order_push_status=#{orderPushStatus}
        and t1.LOGISTIC_NO = #{logisticNo}
        and t1.create_time >= #{createTimeBegin,jdbcType=DATE}
        and t1.create_time <= #{createTimeEnd,jdbcType=DATE}
      	and t1.shipping_time>=#{shippingTimeBegin,jdbcType=DATE}
      	and t1.shipping_time<=#{shippingTimeEnd,jdbcType=DATE}
        AND T1.TRANSFER_TO IN ()
      
	 ) TV ORDER BY TV.createTime desc

      SELECT * FROM (
        SELECT A.*, rownum r
        FROM (
           SELECT
           t1.id                   AS transferId,
           t1.warehouse_id         AS warehouseId,
           t3.warehouse_name       AS transferTo,
           t1.remark               AS remark,
           t1.status               AS status,
           t1.create_time          AS createTime,
           t1.logistic_no          AS logisticNo,
           t1.handled_by           AS handledBy,
           t1.consignee            AS consignee,
           t1.contact              AS contact,
           t1.transfer_sale        AS transferSale,
           t1.transfer_send        AS transferSend,
           t1.transfer_invoice     AS transferInvoice,
           t1.order_amount         AS orderAmount,
           t1.order_push_status    AS orderPushStatus,
           t1.order_confirm_status AS orderConfirmStatus,
           t2.warehouse_name       AS warehouseName
         FROM
           wms_transfer t1
           LEFT JOIN wms_warehouse t2 ON t1.warehouse_id = t2.id
           LEFT JOIN wms_warehouse t3 ON t1.transfer_to = t3.id
		
			t1.id=#{transferId}
			and t1.warehouse_id=#{warehouseId}
			and t1.status=#{status}
			and t1.transfer_to=#{transferTo}
			and t1.order_push_status=#{orderPushStatus}
            and t1.LOGISTIC_NO = #{logisticNo}
            and t1.create_time >= #{createTimeBegin,jdbcType=DATE}
            and t1.create_time <= #{createTimeEnd,jdbcType=DATE}
            and t1.shipping_time >= #{shippingTimeBegin,jdbcType=DATE}
            and t1.shipping_time <= #{shippingTimeEnd,jdbcType=DATE}
            AND T1.TRANSFER_TO IN ()
		
        ORDER BY t1.create_time DESC
		) A
      
          WHERE rownum<=#{page.endRow}
		  )
      WHERE r >= #{page.startRow}

select count(1) as total from wms_transfer t1

    t1.id=#{transferId}
    and t1.warehouse_id=#{warehouseId}
    and t1.transfer_to=#{transferTo}
    and t1.status=#{status}
    and t1.order_push_status=#{orderPushStatus}
    and t1.LOGISTIC_NO = #{logisticNo}
    and t1.create_time >= #{createTimeBegin,jdbcType=DATE}
    and t1.create_time <= #{createTimeEnd,jdbcType=DATE}
    and t1.shipping_time >= #{shippingTimeBegin,jdbcType=DATE}
    and t1.shipping_time <= #{shippingTimeEnd,jdbcType=DATE}
    AND T1.TRANSFER_TO IN ()

		select count(1) as total from wms_transfer t1
		
			t1.id=#{transferId}
			and t1.transfer_to like '%${transferTo}%'
			and t1.warehouse_id=#{warehouseId}
			and t1.status=#{status}
			and t1.transfer_to != #{transferTo}
			and t1.logistic_no=#{logisticNo}
            and t1.create_time >= #{createTimeBegin}
            and t1.create_time <= #{createTimeEnd}
			and t1.ORDER_PUSH_STATUS is null

mapper.insert:
insert into wms_transfer_goods(id, transfer_id,sku_id, sku_code,sku_name,unit_price,quantity,measure_unit,indiv_enabled,qty) select WMS_SEQ_COMMON.nextval,A.* from (
select #{item.transferId},#{item.skuId},#{item.skuCode},#{item.skuName},#{item.unitPrice},#{item.quantity},#{item.measureUnit},#{item.indivEnabled},#{item.qty} from dual
)A

mapper.update:
update wms_transfer
warehouse_id=#{warehouseId, jdbcType=VARCHAR}, transfer_to=#{transferTo, jdbcType=VARCHAR}, remark=#{remark, jdbcType=VARCHAR}, consignee=#{consignee, jdbcType=VARCHAR}, contact=#{contact, jdbcType=VARCHAR}, po=#{po, jdbcType=VARCHAR}, logistic_name=#{logisticName, jdbcType=VARCHAR}, transfer_sale=#{transferSale, jdbcType=VARCHAR}, transfer_send=#{transferSend, jdbcType=VARCHAR}, transfer_invoice=#{transferInvoice, jdbcType=VARCHAR}, order_amount=#{orderAmount, jdbcType=INTEGER}, trans_type=#{transType, jdbcType=VARCHAR}, flow_type=#{flowType, jdbcType=VARCHAR}, shipping_time=#{shippingTime}, status=#{status, jdbcType=VARCHAR}, logistic_no=#{logisticNo, jdbcType=VARCHAR}, handled_by=#{handledBy, jdbcType=VARCHAR}
where id=#{transferId}

update WMS_TRANSFER t1
t1.warehouse_id=#{warehouseId}, t1.transfer_to=#{transferTo}, t1.remark=#{remark}, t1.status=#{status}, t1.order_push_status=#{orderPushStatus}, t1.order_confirm_status=#{orderConfirmStatus}, t1.create_time=sysdate, t1.handled_by=#{handledBy}, t1.consignee=#{consignee}, t1.contact=#{contact}, t1.order_amount=#{orderAmount}, t1.trans_type=#{transType}, t1.LOGISTIC_NO = #{logisticNo}
t1.id=#{transferId}

	update WMS_TRANSFER_GOODS t1
	
