mapper.select:
	SELECT *
	 FROM( 
		  SELECT A.*, rownum r
	  	  FROM(

				  from wms_purchase t1

			   		t1.id=#{id}
			   		and t1.purchase_code=#{purchaseCode}
			   		and t1.warehouse_id=#{warehouseId}
			   		and t1.supplier_id=#{supplierId}
			   		and t1.handling_status=#{handlingStatus}
			   		and t1.prepared_time>=#{preparedTimeBegin}
			   		and t1.prepared_time<=#{preparedTimeEnd}

	  	  	   ) A

		  WHERE rownum<=#{page.endRow} 
		)
    WHERE r >= #{page.startRow}

select count(1) as total
	from wms_purchase t1

   		t1.id=#{id}
   		and t1.purchase_code=#{purchaseCode}
   		and t1.warehouse_id=#{warehouseId}
   		and t1.supplier_id=#{supplierId}
   		and t1.handling_status=#{handlingStatus}
   		and t1.prepared_time>=#{preparedTimeBegin}
   		and t1.prepared_time<=#{preparedTimeEnd}

mapper.update:
update wms_purchase
handling_status=#{handlingStatus}, handled_time=#{handledTime}, handled_by=#{handledBy},
where id = #{id}

mapper.insert:
insert into wms_purchase_goods (id,purchase_id,sku_id,sku_code,sku_name,quantity,measure_unit,product_batchno,indiv_enabled) select WMS_SEQ_COMMON.nextval,A.* from(
select #{item.purchase.id},#{item.skuId},#{item.skuCode},#{item.skuName},#{item.quantity},#{item.measureUnit},#{item.productBatchNo, jdbcType=VARCHAR},#{item.indivEnabled} from dual
)A
